import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './RuleList.css'; // Import the CSS file

function RuleList() {
    const [rules, setRules] = useState([]);

    useEffect(() => {
        const fetchRules = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/rules');
                setRules(response.data);
            } catch (error) {
                alert('Error fetching rules');
            }
        };
        fetchRules();
    }, []);

    return (
        <div className="rule-list">
            <h2>Rule List</h2>
            <ul>
                {rules.map((rule) => (
                    <li key={rule.id}>
                        <strong>Rule ID:</strong> {rule.id} <br />
                        <strong>Name:</strong> {rule.name} <br />
                        <strong>Rule String:</strong> {rule.rule_string}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default RuleList;
