import React, { useState } from 'react';
import axios from 'axios';

function EvaluateForm() {
    const [data, setData] = useState('');
    const [result, setResult] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const parsedData = JSON.parse(data);
            const response = await axios.post('http://localhost:3000/api/rules/evaluate', { data: parsedData });
            setResult(response.data.eligible ? 'Eligible' : 'Not Eligible');
        } catch (error) {
            alert('Error evaluating rule');
            setResult(null);
        }
    };

    return (
        <div>
            <h2>Evaluate Rule</h2>
            <form onSubmit={handleSubmit}>
                <textarea
                    placeholder='Enter JSON data e.g. {"age": 35, "department": "Sales", "salary": 60000, "experience": 3}'
                    value={data}
                    onChange={(e) => setData(e.target.value)}
                    required
                />
                <button type="submit">Evaluate</button>
            </form>
            {result !== null && <p>Result: {result}</p>}
        </div>
    );
}

export default EvaluateForm;
