import React, { useState } from 'react';
import axios from 'axios';

function EvaluateForm() {
    const [data, setData] = useState('');
    const [rule, setRule] = useState(''); // New state for rule input
    const [result, setResult] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const parsedData = JSON.parse(data);

            // Create payload with dynamic rule and data
            const payload = {
                rule: rule.trim(), // Use the input rule
                data: parsedData
            };

            const response = await axios.post('http://localhost:3000/api/rules/evaluate', payload);
            console.log(response.data); // Check the full response for debugging
            setResult(response.data.eligible ? 'True' : 'False');
        } catch (error) {
            console.error('Error evaluating rule:', error);
            alert('Error evaluating rule');
            setResult(null);
        }
    };

    return (
        <div>
            <h2>Evaluate Rule</h2>
            <form onSubmit={handleSubmit}>
                <textarea
                    placeholder='Enter JSON data e.g. {"age": 35, "department": "Sales", "salary": 60000, "experience": 3}'
                    value={data}
                    onChange={(e) => setData(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder='Enter rule e.g. age>10'
                    value={rule}
                    onChange={(e) => setRule(e.target.value)}
                    required
                />
                <button type="submit">Evaluate</button>
            </form>
            {result !== null && <p>Result: {result}</p>}
        </div>
    );
}

export default EvaluateForm;
